mapper.select:
select

from mf_rule_info


    id = #{id,jdbcType=BIGINT}


    and task_id = #{taskId,jdbcType=BIGINT}


    and dataset_id = #{datasetId,jdbcType=INTEGER}


    and rule_type = #{ruleType,jdbcType=INTEGER}


    and rule_name = #{ruleName,jdbcType=VARCHAR}


    and `label` = #{label,jdbcType=INTEGER}


    and label_des = #{labelDes,jdbcType=VARCHAR}


    and accuracy = #{accuracy,jdbcType=VARCHAR}


    and coverage = #{coverage,jdbcType=VARCHAR}


    and conflict = #{conflict,jdbcType=VARCHAR}


    and completed = #{completed,jdbcType=TINYINT}


    and unlabeled_coverage = #{unlabeledCoverage,jdbcType=VARCHAR}


    and overlap = #{overlap,jdbcType=VARCHAR}


    and metadata = #{metadata,jdbcType=LONGVARCHAR}


    and create_datetime = #{createDatetime,jdbcType=TIMESTAMP}


    and create_start_time = #{createStartTime,jdbcType=TIMESTAMP}


    and create_end_time = #{createEndTime,jdbcType=TIMESTAMP}


    and update_datetime = #{updateDatetime,jdbcType=TIMESTAMP}


    and update_start_time = #{updateStartTime,jdbcType=TIMESTAMP}


    and update_end_time = #{updateEndTime,jdbcType=TIMESTAMP}


    and auto_generated = #{autoGenerated,jdbcType=TINYINT}

mapper.delete:
DELETE
FROM mf_rule_info WHERE


    and id = #{id,jdbcType=BIGINT}


    and task_id = #{taskId,jdbcType=BIGINT}


    and dataset_id = #{datasetId,jdbcType=INTEGER}


    and rule_type = #{ruleType,jdbcType=INTEGER}


    and rule_name = #{ruleName,jdbcType=VARCHAR}


    and `label` = #{label,jdbcType=INTEGER}


    and label_des = #{labelDes,jdbcType=VARCHAR}


    and accuracy = #{accuracy,jdbcType=VARCHAR}


    and coverage = #{coverage,jdbcType=VARCHAR}


    and conflict = #{conflict,jdbcType=VARCHAR}


    and completed = #{completed,jdbcType=TINYINT}


    and unlabeled_coverage = #{unlabeledCoverage,jdbcType=VARCHAR}


    and overlap = #{overlap,jdbcType=VARCHAR}


    and metadata = #{metadata,jdbcType=LONGVARCHAR}


    and create_datetime = #{createDatetime,jdbcType=TIMESTAMP}


    and create_start_time = #{createStartTime,jdbcType=TIMESTAMP}


    and create_end_time = #{createEndTime,jdbcType=TIMESTAMP}


    and update_datetime = #{updateDatetime,jdbcType=TIMESTAMP}


    and update_start_time = #{updateStartTime,jdbcType=TIMESTAMP}


    and update_end_time = #{updateEndTime,jdbcType=TIMESTAMP}


    and auto_generated = #{autoGenerated,jdbcType=TINYINT}
